def produce_external_table_definition_from_delta_schema(recreate_external_tables:bool, table_name: str, schema_name: str, database_name: str, location: str, data_source:str, data_lake:str,container_name:str) -> str:
    """
    Generates a T-SQL CREATE EXTERNAL TABLE statement for a given PySpark DataFrame.

    Args:
        recreate_external_tables (bool): Takes in the constant from (PARAM_RECREATE_EXTERNAL_TABLES)
        table_name (str): The name of the table to be created.
        schema_name (str): The schema name in the SQL database.
        database_name (str): The database name in the SQL database.
        location (str): The location of the external data (e.g., Azure Data Lake Storage path).
        data_source (str): The name of the external data source pointing to the right Storage Account & Container where the file exists.
        data_lake (str): The name of the data lake where the files reside.
        container_name (str): The name of the container within the data lake where the files reside.

    Returns:
        str: The T-SQL CREATE EXTERNAL TABLE statement.
    """
    # Compose the full abfss file path fromthe container, data lake and exact location.
    file_path = f'abfss://{container_name}@{data_lake}.dfs.core.windows.net/{location}'
    # Read delta table to dataframe.
    df = spark.read.format("delta").load(file_path)

    # Get the column names and data types from the DataFrame

    columns = df.dtypes
    column_definitions = []

    for column_name, data_type in columns:
        # Map PySpark data types to SQL data types
        if data_type == 'string':
            sql_data_type = 'VARCHAR(MAX)'
        elif data_type == 'int':
            sql_data_type = 'INT'
        elif data_type == 'double':
            sql_data_type = 'FLOAT'
        elif data_type == 'boolean':
            sql_data_type = 'BIT'
        elif data_type == 'timestamp':
            sql_data_type = 'DATETIME'
        else:
            sql_data_type = 'VARCHAR(MAX)'  # Default to VARCHAR(MAX) for unsupported types

        # Append the column definition to the list
        column_definitions.append(f"[{column_name}] {sql_data_type}")

    # Join the column definitions into a single string
    columns_sql = ",\n    ".join(column_definitions)

    # Create the T-SQL statement for creating the external table
    if recreate_external_tables == True:
        create_table_sql = f"""
        USE [{database_name}];
        IF EXISTS (select s.name as [SCHEMA_NAME],t.name AS [TABLE_NAME] from sys.tables t, sys.schemas s where t.schema_id = s.schema_id and s.name = '{schema_name}' and t.name = '{table_name}')
            DROP EXTERNAL TABLE [{schema_name}].[{table_name}];

        CREATE EXTERNAL TABLE [{schema_name}].[{table_name}] (
            {columns_sql}
        )
        WITH (
            LOCATION = '{location}',
            DATA_SOURCE = [{data_source}],
            FILE_FORMAT = [exff_delta]
        );
        """
    else:
        create_table_sql = f"""
        USE [{database_name}];
        IF NOT EXISTS (select s.name as [SCHEMA_NAME],t.name AS [TABLE_NAME] from sys.tables t, sys.schemas s where t.schema_id = s.schema_id and s.name = '{schema_name}' and t.name = '{table_name}')
            CREATE EXTERNAL TABLE [{schema_name}].[{table_name}] (
                {columns_sql}
            )
            WITH (
                LOCATION = '{location}',
                DATA_SOURCE = [{data_source}],
                FILE_FORMAT = [exff_delta]
            );
        """


    return create_table_sql
